import { chairList } from "../../chairList";
import { InputSeat } from "../Constants/Constants";

let initialState = {
  seatsInput: 0,
  selectedSeat: {
    taken: Boolean(Math.round(Math.random())),
    checked: false,
    value: "A1",
  },
  chairList: chairList,
};

export const seatHandler = (state = initialState, action) => {
  switch (action.type) {
    case InputSeat: {
      const chairList = action.payload;
      let chair = chairList.map((chair, key) => {
        return <div>{chair.hang}</div>;
      });
      document.getElementById("idTable").innerHTML = chair;
    }
    default: {
      return state;
    }
  }
};
