import { chairList } from "../../chairList";
import { InputSeat } from "../Constants/Constants";

let initialState = {
  seatsInput: 0,
  ticketCart: [],
  chairList: chairList,
};

export const seatHandler = (state = initialState, action) => {
  switch (action.type) {
    case InputSeat: {
      const newchairList = [...state.chairList];
      const newticketCart = [...state.ticketCart];
      var chair = action.payload;
      chair.dangChon = true;
      // Add a click event listener to each element
      const seatsElements = document.querySelectorAll(".seats");
      if (newchairList.length == 0) {
        newticketCart.push(chair);
      } else {
        for (let i = 0; i <= newticketCart.length; i++) {
          const checkSelected = chair == newticketCart[i];

          if (checkSelected === false) {
            newticketCart.push(chair);
            console.log(
              "ðŸš€ ~ file: ReducerSeat.js:25 ~ seatHandler ~ newticketCart:",
              newticketCart
            );
          }
        }
      }

      seatsElements.forEach((element, index) => {
        element.addEventListener("click", (event) => {
          // This code will be executed when a ".seats" element is clicked
          console.log("Element clicked!", index);

          // Change the background color of the clicked element
          element.style.backgroundColor = "green";
        });
      });
      console.log(chair.dangChon ? chair.dangChon.toString() : "false");

      state.chairList = newchairList;
      state.ticketCart = newticketCart;
      return { ...state };
    }
    default: {
      return state;
    }
  }
};
