import React, { Component } from "react";
import { connect } from "react-redux";
import { InputSeat } from "./redux/Constants/Constants";
import { chairList } from "./chairList";
class SquareOfCheckboxTable extends Component {
  render() {
    return (
      <div id="idTable">
        {this.props.chairList.map((rowChair, key) => {
          return (
            <div key={key}>
              {rowChair.hang}
              <span>
                {rowChair.danhSachGhe.map((seat, seatIndex) => (
                  <p key={seatIndex}>
                    Seat: {seat.soGhe}, Price: {seat.gia}, Booked:{" "}
                    {/* {seat.daDat.toString()} */}
                  </p>
                ))}
              </span>
              {/* {rowChair.danhSachGhe} */}

              {/* {rowChair.danhSachGhe.map((index) => {
                return <div>{index}</div>;
              })} */}
            </div>
          );
        })}
      </div>
    );
  }
}

let mapStateToProps = (state) => {
  return {
    chairList: state.chairList,
  };
};

export default connect(mapStateToProps)(SquareOfCheckboxTable);
