import { chairList } from "../../chairList";
import { InputSeat } from "../Constants/Constants";

let initialState = {
  seatsInput: 0,
  tiketCart: [],
  chairList: chairList,
};

export const seatHandler = (state = initialState, action) => {
  switch (action.type) {
    case InputSeat: {
      const newchairList = [...state.chairList];
      const button = document.querySelector(".seats");
      chair.dangChon = true;

      button.addEventListener("click", (evt) => {
        const clickedButton = evt.target.key;
        evt.target.style.backgroundColor = "green";
      });
      button.style.backgroundColor = "green";
      var chair = action.payload;
      console.log(chair.dangChon ? chair.dangChon.toString() : "false");
      console.log(chair.dangChon ? chair.dangChon.toString() : "false");
      state.chairList = newchairList;
      return { ...state };
    }
    default: {
      return state;
    }
  }
};
