import { shoeArr } from "../../../Ex_Shoe/data";
let initialState = {
  shoeArr: shoeArr,
  detail: shoeArr[0],
  cart: [],
};

export let shoeReducer = (state = initialState, action) => {
  switch (action.type) {
    case "VIEW_DETAIL":
      return { ...state, detail: action.payload };
    case "ADD_TO_CART":
      return {
        ...state,
        cart: action.payload,
      };
    default:
      return state;
  }
};
