import React, { Component } from "react";
import { connect } from "react-redux";
class ItemShoe extends Component {
  render() {
    let data = this.props.data;
    let cart = this.props.data;
    return (
      <div className="col-3 card">
        <img className="w-100" src={data?.image} alt="" />
        <br />

        <button
          className="btn btn-primary"
          onClick={() => {
            this.props.addToCart(data);
          }}
        >
          Add
        </button>
        <button
          onClick={() => {
            this.props.handleClickView(data);
          }}
          className="btn btn-info"
        >
          View
        </button>
      </div>
    );
  }
}
// add to cart

// dispatch lên store để update
const mapStateToProps = (state) => {
  // console.log(state.shoeReducer.shoeArr);
  return {
    shoeArr: state.shoeReducer.shoeArr,
    cart: state.shoeReducer.cart,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    handleClickView: (data) => {
      let action = {
        type: "VIEW_DETAIL",
        payload: data,
      };
      dispatch(action);
    },
    addToCart: (value) => {
      let action = {
        type: "ADD_TO_CART",
        payload: value,
      };
      dispatch(action);
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(ItemShoe);
