import React, { Component } from "react";

export default class ChairList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: 12,
    };
  }

  renderChairRow = () => {
    const { numRows } = this.state;
    const rows = [];

    for (let i = 2; i <= numRows; i++) {
      const cells = [];
      for (let j = 1; j <= numRows; j++) {
        cells.push(<th key={j}>{j}</th>);
      }
      rows.push(
        <tr key={i}>
          <th scope="row">{i}</th>
          {cells}
        </tr>
      );
    }

    return rows;
  };

  render() {
    return (
      <div className="Ds_ghe">
        <table className="w-100">
          <thead>
            <tr>
              {[...Array(this.state.numRows).keys()].map((num) => (
                <th key={num} className="text-warning">
                  {num + 1}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>{this.renderChairRow()}</tbody>
        </table>
      </div>
    );
  }
}
