import React, { Component } from "react";
import { connect } from "react-redux";
import { InputSeat } from "./redux/Constants/Constants";
import { chairList } from "./chairList";
class SquareOfCheckboxTable extends Component {
  render() {
    return (
      <div id="idTable">
        {this.props.chairList.map((chair) => {
          return <div>{chair.hang}</div>;
        })}
      </div>
    );
  }
}

let mapStateToProps = (state) => {
  return {
    chairList: state.chairList,
  };
};
let mapDispatchToProps = (dispatch) => {
  return {
    handleChairList: (item) => {
      let action = {
        type: InputSeat,
        payload: item,
      };
      dispatch(action);
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SquareOfCheckboxTable);
