import React, { Component } from "react";
import { connect } from "react-redux";
class ItemShoe extends Component {
  render() {
    let { data, handleClickView, addToCart } = this.props;
    return (
      <div className="col-3 card">
        <img className="w-100" src={data?.image} alt="" />
        <br />

        <button className="btn btn-primary" onClick={() => addToCart(data)}>
          Add
        </button>
        <button
          onClick={() => {
            handleClickView(data);
          }}
          className="btn btn-info"
        >
          View
        </button>
      </div>
    );
  }
}

// dispatch lên store để update

const mapDispatchToProps = (dispatch) => {
  return {
    handleClickView: (shoe) => {
      let action = {
        type: "VIEW_DETAIL",
        payload: shoe,
      };
      dispatch(action);
    },
    addToCart: (shoe) => {
      let action = {
        type: "ADD_TO_CART",
        payload: shoe,
      };
      dispatch(action);
    },
  };
};
export default connect(null, mapDispatchToProps)(ItemShoe);

export let handleCart = (shoe) => {
  // spread operator
  // const newCart = [...this.state.cart, shoe]

  const newCart = this.state?.cart;

  // Kiểm tra trong cart đã tồn tại sản phẩm hay chưa
  const index = newCart.findIndex((value) => value.id === shoe.id);

  if (index !== -1) {
    // sản phẩm đã có trong cart => chỉ thay đổi thuộc tính cartQuantity
    newCart[index].cartQuantity += 1;
  } else {
    // sản phẩm chưa có trong cart => thêm vào cart
    newCart.push({ ...shoe, cartQuantity: 1 });
  }

  // console.log("newCart: ", newCart);

  this.setState({
    cart: newCart,
  });
};
